import React, { useState } from "react";
import "./LandingPage.css";

function LandingPage() {
  const [click, setClick] = useState(false);

  return (
    <div id={click ? "landing__container-dark" : "landing__container"}>
      <div className={click ? "landing__body-dark" : "landing__body"}>
        {/* <img src="data:image/svg+xml;base64," /> */}
        <i className="fas fa-laptop-code"></i>
        <div className="landing__head">김승무</div>
        <div className="landing__content">프론트엔드 엔지니어</div>
        <label className="landing__switch">
          <input onClick={() => setClick(!click)} type="checkbox" />
          <span className="slider round"></span>
          <img
            className={click ? "slider__icon-sun" : "blind"}
            src="data:image/svg+xml;base64,"
          />
          <img
            className={click ? "blind" : "slider__icon-moon"}
            src="data:image/svg+xml;base64,"
          />
        </label>
      </div>
    </div>
  );
}

export default LandingPage;
