import React, { useState, useEffect } from "react";
import "./LandingPage.css";
import github from "../images/github-big-.png";
import arm from "../images/github-big-arm.png";
import monitor from "../images/monitor1.svg";

function LandingPage({ click, setClick }) {
  const [count, setCount] = useState(0);
  const [text, setText] = useState("");
  const txt = "프론트엔드 개발자 김승무입니다.";

  useEffect(() => {
    const typing = setInterval(() => {
      setText(text + txt[count]);
      setCount(count + 1);
    }, 150);
    if (txt.length === count) clearInterval(typing);
    return () => clearInterval(typing);
  });

  return (
    <div id={click ? "landing__container-dark" : "landing__container"}>
      <a
        className="github"
        href="https://github.com/KimseungMoo"
        target="_blank"
      >
        <div className="triangle"></div>
        <img className="github__logo" src={github} />
        <img className="github__arm" src={arm} />
      </a>
      <div className={click ? "landing__body-dark" : "landing__body"}>
        <div className="landing__body-monitor">
          <img className="monitor" src={monitor}></img>
          <label className="landing__switch">
            <input onClick={() => setClick(!click)} type="checkbox" />
            <span className="slider"></span>
            <img
              className={click ? "slider__icon-sun" : "blind"}
              src="data:image/svg+xml;base64,"
            />
            <img
              className={click ? "blind" : "slider__icon-moon"}
              src="data:image/svg+xml;base64,"
            />
          </label>
        </div>
        <div className="landing__head">
          {text}
          <span className="blink">|</span>
        </div>
        {/* <div className="in__progress">작업 진행 중...</div> */}
      </div>
      <span className={click ? "landing__mouse-dark" : "landing__mouse"}></span>
    </div>
  );
}

export default LandingPage;
